@RestController
public class PositiveIntegersController {

    @GetMapping("/positiveIntegers")
    public int positiveIntegers() {
        int count = 0;
        for (int m = 1; m <= 1000; m++) {
            if (existsPositiveIntegerN(m)) {
                count++;
            }
        }
        return count;
    }

    private boolean existsPositiveIntegerN(int m) {
        // We want to find if there exists a positive integer n such that m * n <= m + n
        // Rearrange to n(m-1) <= m, so if (m-1) is not zero, n <= m / (m-1)
        // This will be true for some n if m / (m-1) >= 1, which simplifies to m >= 2
        return m >= 2;
    }

    public static void main(String[] args) {
        PositiveIntegersController controller = new PositiveIntegersController();
        System.out.println(controller.positiveIntegers());
    }
}

