.getTotalWays = function(choco, sugar) {
    return factorial(choco + sugar) / (factorial(choco) * factorial(sugar));
};

getDesiredWays = function(choco, sugar) {
    totalWays = factorial(choco + sugar);
    chocoFirst = factorial(choco - 1) * factorial(sugar) * choco;
    chocoLast = factorial(choco) * factorial(sugar - 1) * sugar;
    both = factorial(choco - 1) * factorial(sugar - 1) * choco * sugar;

    // Avoid double counting
    return totalWays - (chocoFirst + chocoLast - both);
};

