import java.util.HashSet;
import java.util.Set;

public class Main {
    public static void main(String[] args) {
        int product = 2310;
        int factorsCount = 0;

        // Iterate over possible values of a and b
        for (int a = 1; a <= Math.pow(product, 1.0/3); a++) {
            if (product % a == 0) {
                int remainingProduct = product / a;
                for (int b = a + 1; b <= Math.sqrt(remainingProduct); b++) {
                    if (remainingProduct % b == 0) {
                        int c = remainingProduct / b;
                        factorsCount++;
                    }
                }
            }
        }

        // Since each set {a,b,c} is counted 6 times (3! permutations), divide by 6.
        factorsCount /= 6;

        System.out.println(factorsCount);
    }
}
