using System.Numerics;

public class Solution {
    public static void Main() {
        BigInteger sum = 0;
        int minN = int.MaxValue;
        int maxN = int.MinValue;

        for (BigInteger n = 34; ; n++) {
            BigInteger diff = n * n - 33 * 33;
            if (diff < 0) continue;
            BigInteger sqrt = BigInteger.ModSqrt(diff, 1);
            if (sqrt * sqrt == diff) {
                int nInt = (int)n;
                if (nInt < minN) minN = nInt;
                if (nInt > maxN) maxN = nInt;
            }
            if (n > 1000000) break; // 큰 수를 탐색하는 데 걸리는 시간을 줄이기 위함
        }

        sum = minN + maxN;
        Console.WriteLine(sum.ToString());
    }
}

