        int sum = findSmallestSumOfFourConsecutiveEvenNumbers();
        System.out.println(sum);
    }

    public static int findSmallestSumOfFourConsecutiveEvenNumbers() {
        int n = 1;
        while (true) {
            int sum = 4 * n + 6; // Sum of four consecutive even numbers: (n-2) + (n) + (n+2) + (n+4) = 4n + 4
            double sqrt = Math.sqrt(sum);
            if (sqrt == Math.floor(sqrt)) {
                return sum;
            }
            n++;
        }
    }
}

