// Let's define a recursive function to count the valid sequences.
long count_sequences(int pos, int last_even, int last_odd) {
    if (pos == 11) {
        return 1; // If we reach the end, we have a valid sequence.
    }
    long count = 0;
    if (pos % 2 == 1) { // If the position is odd (1, 3, 5, 7, 9)
        for (int i = 1; i <= 3; i++) {
            if (i > last_odd) {
                count += count_sequences(pos + 1, i, i);
            }
        }
    } else { // If the position is even (2, 4, 6, 8)
        for (int i = 1; i <= 3; i++) {
            if (i <= last_even) {
                count += count_sequences(pos + 1, i, i);
            }
        }
    }
    return count;
}

// Start the counting from the first position with no previous values set.
long result = count_sequences(1, 0, 0);
printf("The number of valid sequences is: %ld\n", result);
