typealias A = Double
typealias B = Double
typealias C = Double
typealias Point = (A, B)

func solve() -> C {
    let AJ = 90.0
    let DJ = 60.0
    let AI = AJ - DJ // AI의 길이는 AJ와 DJ의 차이

    // AI와 DJ를 이용해 CI를 구함
    let CI = AI * 2 // CI의 길이는 AI의 두 배

    // 삼각형 BCI의 외접원 반지름 구하기
    // 외접원의 반지름 R은 BC의 길이 a와 CI의 길이 c를 이용해 R = a * c / (4 * [BCI])로 구할 수 있다.
    // 여기서 [BCI]는 삼각형 BCI의 면적을 의미한다.
    // 삼각형 BCI의 높이는 CI이고 밑변은 BC의 길이 a이다.
    // 따라서 [BCI] = 1/2 * a * CI = 1/2 * a * CI
    // BC의 길이는 AI + DJ = 90 + 60 = 150
    let a = AI + DJ
    let areaBCI = 0.5 * a * CI
    let R = a * CI / (4 * areaBCI)

    return R
}

