    // S(2) = f(1,1) + f(2,1) + f(1,2) = 1 + 2 + 1 = 4
    // S(3) = f(1,2) + f(2,1) + f(2,2) + f(1,3) + f(3,1) = 2 + 2 + 3 + 1 + 1 = 9
    // S(4) = f(1,3) + f(2,2) + f(3,1) + f(3,2) + f(2,3) + f(4,1) + f(1,4) = 3 + 3 + 3 + 4 + 4 + 1 + 1 = 19

