 typealias RealSequence = [Double]

func findAN5() -> Double {
    // Initialize the sequence with the given values
    var sequence: RealSequence = [1.0] // a1 = 1

    // Calculate the subsequent terms of the sequence until a9
    for n in 1..<9 {
        let an = sequence[n]
        let anMinus1 = sequence[n-1]
        let anPlus1 = (an * an - anMinus1 + 2 * an) / (anMinus1 + 1)
        sequence.append(anPlus1)
    }

    return sequence[4] // a5
}

