public class PermutationGenerator {
    public List<List<Integer>> generatePermutations(List<Integer> values, int n) {
        List<List<Integer>> result = new ArrayList<>();
        permute(values, 0, n, result);
        return result;
    }

    private void permute(List<Integer> values, int start, int n, List<List<Integer>> result) {
        if (start == n) {
            result.add(new ArrayList<>(values));
        } else {
            for (int i = start; i < n; i++) {
                Collections.swap(values, i, start);
                permute(values, start + 1, n, result);
                Collections.swap(values, start, i);
            }
        }
    }
}
